const body = $json.body || $json;
const msgs = body.messages || [];
let q = body.query;
if (!q && msgs.length) {
  for (let i = msgs.length - 1; i >= 0; i--) {
    if (msgs[i].role === 'user') { q = msgs[i].content; break; }
  }
}
q = q || '';
return [{ json: { query: q, embedRequestBody: { model: $env.OLLAMA_EMBED_MODEL || 'nomic-embed-text', prompt: q } } }];

const emb = $json.embedding || [];
const embVec = '[' + emb.join(',') + ']';
return [{ json: { query: $('Extract Query').first().json.query, embedding_vec: embVec } }];

const hits = ($('Vector Search (Postgres)').all() || []).map(it => it.json);
const base = $env.PUBLIC_ASSETS_BASE_URL || '';
const images = hits
  .filter(h => h.chunk_type === 'image' && h.asset_path)
  .map(h => `${base}/${h.asset_path}`);
const ctxLines = hits.map(h => {
  if (h.chunk_type === 'image') {
    const url = h.asset_path ? `${base}/${h.asset_path}` : '';
    return `Bild (Seite ${h.page}): ${h.caption || ''} ${url}`.trim();
  }
  return `Text (Seite ${h.page}): ${h.content_text || ''}`;
});
const sources = hits.map(h => `Seite ${h.page} (${h.chunk_type})`).slice(0, 8);
const query = $('Extract Query').first().json.query;
const context = ctxLines.join('\n\n');
const chatRequestBody = {
  model: $env.OLLAMA_TEXT_MODEL || 'llama3.1:8b',
  stream: false,
  options: { num_predict: 400 },
  messages: [
    {role: 'system', content: 'Du bist ein präziser Assistent. Antworte auf Deutsch. Zitiere Seitenzahlen wenn möglich.'},
    {role: 'user', content: 'Frage: ' + query + '\n\nKontext aus Dokumenten:\n' + context + '\n\nAntworte kurz, korrekt und mit Seitenhinweisen.'}
  ]
};
return [{ json: { query, context, images, sources, chatRequestBody } }];

const msg = $json.message?.content || '';
const ctx = $('Build Context').first().json;
return [{ json: { answer: msg, images: ctx.images || [], sources: ctx.sources || [] } }];
